function closeAlert(button){const alert=button.closest('.smd-component_alert');alert.style.opacity='0';alert.style.transform='translateX(100%)';alert.style.transition='all 0.3s ease';setTimeout(()=>{alert.remove();},300);}function unfocus(element){document.getElementById(element).addEventListener('blur',function(){this.classList.add('smd-component_textfield--unfocus');});}function hideElement(element){var label=document.getElementById(element);label.classList.add("hidden");}function showElement(element){var label=document.getElementById(element);label.classList.remove("hidden");}function toggleSwitch(element){element.classList.toggle('smd-component_toggle--active');}function toggleLoading(button){if(button.classList.contains('smd-component_button--loading')){button.classList.remove('smd-component_button--loading');button.classList.add('smd-component_button--loading-out');setTimeout(()=>{button.classList.remove('smd-component_button--loading-out');button.classList.remove('interactive-loading');button.innerHTML=button.dataset.originalText || 'Toggle Loading';button.disabled=false;button.style.minWidth='';button.style.height='';},400);}else{button.dataset.originalText=button.innerHTML;const currentWidth=button.offsetWidth;const currentHeight=button.offsetHeight;button.style.minWidth=currentWidth + 'px';button.style.height=currentHeight + 'px';button.innerHTML=`<span class="button-text">${button.innerHTML}</span>`;button.classList.add('interactive-loading');setTimeout(()=>{button.classList.add('smd-component_button--loading');button.disabled=true;},10);}}function toggleDropdown(btn){const container=btn.closest('.smd-component_dropdown');const dropdown=container.querySelector('.smd-component_dropdown-content');dropdown.classList.toggle('show');document.querySelectorAll('.smd-component_dropdown-content').forEach(otherDropdown=>{if(otherDropdown !==dropdown){otherDropdown.classList.remove('show');}});}document.addEventListener('click',function(event){if(!event.target.closest('.smd-component_dropdown')){document.querySelectorAll('.smd-component_dropdown-content.show').forEach(dropdown=>{dropdown.classList.remove('show');});}});document.addEventListener('keydown',function(event){if(event.key==='Escape'){document.querySelectorAll('.smd-component_dropdown-content.show').forEach(dropdown=>{dropdown.classList.remove('show');});}});document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('.smd-component_rating').forEach(rating=>{const stars=rating.querySelectorAll('.smd-component_rating-star');stars.forEach(star=>{star.addEventListener('click',()=>{const value=parseInt(star.dataset.value);rating.dataset.rating=value;stars.forEach((s,index)=>{if(index < value){s.classList.add('smd-component_rating-star--filled');}else{s.classList.remove('smd-component_rating-star--filled');}});});star.addEventListener('mouseenter',()=>{const value=parseInt(star.dataset.value);stars.forEach((s,index)=>{if(index < value){s.style.color='var(--color-branding-primary)';}else{s.style.color='';}});});});rating.addEventListener('mouseleave',()=>{stars.forEach(star=>{star.style.color='';});});});});function showTab(event,tabId){document.querySelectorAll('.smd-component_tabs-content').forEach(content=>{content.classList.remove('smd-component_tabs-content--active');});document.querySelectorAll('.smd-component_tabs-tab').forEach(tab=>{tab.classList.remove('smd-component_tabs-tab--active');});document.getElementById(tabId).classList.add('smd-component_tabs-content--active');event.target.classList.add('smd-component_tabs-tab--active');}function showToast(type){const existingToasts=document.querySelectorAll('.smd-component_toast');existingToasts.forEach(toast=> toast.remove());const toast=document.createElement('div');toast.className='smd-component_toast';switch(type){case 'success':toast.classList.add('smd-component_toast--success');toast.textContent='✅ Success! Operation completed.';break;case 'error':toast.classList.add('smd-component_toast--error');toast.textContent='❌ Error! Something went wrong.';break;default:toast.textContent='ℹ️ This is a notification.';}document.body.appendChild(toast);setTimeout(()=>{toast.remove();},3000);}